SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_diretor_ups ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_proc_principal_w		bigint;
ie_origem_proc_princ_w		bigint;
cd_estabelecimento_W		smallint;
cd_grupo_w			bigint;
cd_especialidade_w		bigint;
cd_area_w			bigint;

cd_pessoa_fisica_w		varchar(10);
nm_pessoa_fisica_w		varchar(60);
cd_cns_diretor_w		bigint;

ds_retorno_w			varchar(60);

nr_seq_grupo_w			bigint;
nr_seq_subgrupo_w		bigint;
nr_seq_forma_org_w		bigint;

C01 CURSOR FOR 
	SELECT	cd_pessoa_fisica, 
		substr(obter_nome_pf(cd_pessoa_fisica),1,60), 
		cd_cns_diretor 
	from	sus_apac_diretor_proc 
	where	coalesce(cd_procedimento,cd_procedimento_p)		= cd_procedimento_p 
	and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p 
	and	coalesce(cd_area_procedimento,cd_area_w)		= cd_area_w	 
	and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w 
	and	coalesce(cd_grupo_proc,cd_grupo_w)			= cd_grupo_w 
	and	coalesce(nr_seq_forma_org,nr_seq_forma_org_w)	= nr_seq_forma_org_w 
	and	coalesce(nr_seq_subgrupo,nr_seq_subgrupo_w)		= nr_seq_subgrupo_w	 
	and	coalesce(nr_seq_grupo,nr_seq_grupo_w)		= nr_seq_grupo_w 
	order by 
		coalesce(cd_procedimento,0), 
		coalesce(cd_grupo_proc,0), 
		coalesce(cd_especialidade,0), 
		coalesce(cd_area_procedimento,0), 
		coalesce(nr_seq_forma_org,0), 
		coalesce(nr_seq_subgrupo,0), 
		coalesce(nr_seq_grupo,0);


BEGIN 
 
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
 
select	coalesce(max(nr_seq_grupo),0), 
	coalesce(max(nr_seq_subgrupo),0), 
	coalesce(max(nr_seq_forma_org),0) 
into STRICT	nr_seq_grupo_w, 
	nr_seq_subgrupo_w, 
	nr_seq_forma_org_w 
from	sus_estrutura_procedimento_v 
where	cd_procedimento 	= cd_procedimento_p 
and	ie_origem_proced	= ie_origem_proced_p;
 
select	coalesce(max(cd_grupo_proc),0), 
	coalesce(max(cd_especialidade),0), 
	coalesce(max(cd_area_procedimento),0) 
into STRICT	cd_grupo_w, 
	cd_especialidade_w, 
	cd_area_w 
from	Estrutura_Procedimento_V 
where	cd_procedimento 	= cd_procedimento_p 
and	ie_origem_proced	= ie_origem_proced_p;
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w, 
	cd_cns_diretor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	cd_pessoa_fisica_w	:= cd_pessoa_fisica_w;
	cd_cns_diretor_w	:= cd_cns_diretor_w;
	nm_pessoa_fisica_w	:= nm_pessoa_fisica_w;
	end;
END LOOP;
CLOSE C01;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	select	max(cd_diretor_clinico) 
	into STRICT	cd_pessoa_fisica_w 
	from	sus_parametros_apac 
	where	cd_estabelecimento	= cd_estabelecimento_W;
end if;
 
ds_retorno_w	:= cd_pessoa_fisica_w;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
