CREATE OR REPLACE FUNCTION sus_obter_doc_compl_pf ( cd_pessoa_fisica_p text, ie_tipo_complemento_p bigint, ie_descricao_p text) RETURNS varchar AS $body$
DECLARE


ds_descricao_w		varchar(255)  := '';

/*
ie_tipo_complemento_p

   1 - residencial
   2 - comercial
   3 - responsável
   4 - pai
   5 - mãe
   6 - conjuge
   7 - comercial 2

ie_descricao_p

   C - nº cpf
   I - nº identidade

*/
BEGIN

begin

if (ie_tipo_complemento_p > 0) then
	begin

	if (ie_descricao_p = 'C') then
	select	nr_cpf
	into STRICT	ds_descricao_w
	from (SELECT a.nr_cpf
		from	compl_pessoa_fisica 	a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.ie_tipo_complemento = ie_tipo_complemento_p
		order	by a.nr_sequencia desc) alias2 LIMIT 1;
	elsif (ie_descricao_p = 'I') then
		select	nr_identidade
		into STRICT	ds_descricao_w
		from (SELECT a.nr_identidade
			from	compl_pessoa_fisica a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
			and	a.ie_tipo_complemento = ie_tipo_complemento_p
			order	by a.nr_sequencia desc) alias1 LIMIT 1;
	end if;
	end;

end if;

exception
when others then
	ds_descricao_w := '';
end;

return	ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
