CREATE OR REPLACE FUNCTION sus_obter_especialidade_aih ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_w		bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_forma_org_w	bigint;
qt_idade_pac_w		integer;
cd_especialidade_aih_w	smallint;
ds_retorno_w		smallint;
			
C01 CURSOR FOR
	SELECT	cd_especialidade_aih
	from	sus_regra_espec_aih_proc
	where	coalesce(nr_seq_grupo, nr_seq_grupo_w)		= nr_seq_grupo_w
	and	coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)		= nr_seq_subgrupo_w
	and	coalesce(nr_seq_forma_org, nr_seq_forma_org_w)	= nr_seq_forma_org_w
	and	coalesce(cd_procedimento, cd_procedimento_p)		= cd_procedimento_p
	and	coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	qt_idade_pac_w	>= coalesce(qt_idade_minima,0)
	and 	qt_idade_pac_w <= coalesce(qt_idade_maxima,999)
        and     ie_situacao = 'A'
	order by coalesce(cd_procedimento, 0),
		coalesce(nr_seq_grupo, 0),
		coalesce(nr_seq_subgrupo, 0),
        coalesce(nr_seq_forma_org, 0);	


		

BEGIN

qt_idade_pac_w	:= obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'A');

begin
select	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org
into STRICT	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w
from	sus_estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
end;

open C01;
loop
fetch C01 into	
	cd_especialidade_aih_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= cd_especialidade_aih_w;
	end;
end loop;
close C01;

return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
