CREATE OR REPLACE FUNCTION sus_obter_especialidade_proced (cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



cd_especialidade_w		integer;
ds_especialidade_w		varchar(60);


BEGIN

select	min(b.cd_espec_leito),
	min(b.ds_espec_leito)
into STRICT	cd_especialidade_w,
	ds_especialidade_w
from    sus_espec_leito         b,
        sus_procedimento_leito  a
where   a.nr_seq_espec_leito    = b.nr_sequencia
and     a.cd_procedimento       = cd_procedimento_p
and     a.ie_origem_proced      = ie_origem_proced_p;

if (ie_opcao_p = 'C') then
	return	cd_especialidade_w;
else
	return	ds_especialidade_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
