CREATE OR REPLACE FUNCTION sus_obter_estrut_cid ( cd_doenca_cid_p cid_doenca.cd_doenca_cid%type, ie_estrut_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_ESTRUT_P:
C - Categiria
G - Grupo
P - Capítulo

IE_OPCAO_P:
C - Código
D - Descrição
*/
ds_retorno_w			varchar(255);
cd_categoria_cid_w		cid_categoria.cd_categoria_cid%type;
ds_categoria_cid_w		cid_categoria.ds_categoria_cid%type;
ds_grupo_cid_w			cid_grupo.ds_grupo_cid%type;
cd_especialidade_cid_w		cid_especialidade.cd_especialidade_cid%type;
ds_especialidade_cid_w		cid_especialidade.ds_especialidade_cid%type;


BEGIN


if (ie_estrut_p = 'C') then
	begin

	select	b.cd_categoria_cid,
		b.ds_categoria_cid
	into STRICT	cd_categoria_cid_w,
		ds_categoria_cid_w
	from	cid_doenca a,
		cid_categoria b
	where	a.cd_doenca_cid = cd_doenca_cid_p
	and	a.cd_categoria_cid = b.cd_categoria_cid  LIMIT 1;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_categoria_cid_w;
	else
		ds_retorno_w := substr(ds_categoria_cid_w,1,255);
	end if;

	end;
elsif (ie_estrut_p = 'G') then
	begin

	select	cd_categoria_cid
	into STRICT	cd_categoria_cid_w
	from	cid_doenca
	where	cd_doenca_cid = cd_doenca_cid_p  LIMIT 1;

	select	cd_categoria_inicial,
		ds_grupo_cid
	into STRICT	cd_categoria_cid_w,
		ds_grupo_cid_w
	from	cid_grupo
	where	cd_categoria_cid_w between cd_categoria_inicial and cd_categoria_final  LIMIT 1;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_categoria_cid_w;
	else
		ds_retorno_w := substr(ds_grupo_cid_w,1,255);
	end if;

	end;
elsif (ie_estrut_p = 'P') then
	begin

	select	c.cd_especialidade_cid,
		c.ds_especialidade_cid
	into STRICT	cd_especialidade_cid_w,
		ds_especialidade_cid_w
	from	cid_doenca a,
		cid_categoria b,
		cid_especialidade c
	where	a.cd_doenca_cid = cd_doenca_cid_p
	and	a.cd_categoria_cid = b.cd_categoria_cid
	and	b.cd_especialidade = c.cd_especialidade_cid  LIMIT 1;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_especialidade_cid_w;
	else
		ds_retorno_w := substr(ds_especialidade_cid_w,1,255);
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
