SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_estr_porc_edit (cd_procedimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_campo_w    		varchar(20);
cd_grupo_w		varchar(15);
cd_subgrupo_w		varchar(15);
cd_forma_organizacao_w	varchar(15);

/*
IE_OPCAO_P:
F - Forma de organização
S - Subgrupo
G - Grupo
*/
BEGIN

select	lpad(a.cd_grupo,2,0),
	lpad(b.cd_subgrupo,4,0),
	lpad(c.cd_forma_organizacao,6,0)
into STRICT	cd_grupo_w,
	cd_subgrupo_w,
	cd_forma_organizacao_w
from	sus_grupo a,
	sus_subgrupo b,
	sus_forma_organizacao c,
	sus_procedimento d
where	a.nr_sequencia = b.nr_seq_grupo
and	b.nr_sequencia = c.nr_seq_subgrupo
and	c.nr_sequencia = d.nr_seq_forma_org
and	d.cd_procedimento = cd_procedimento_p
and	d.ie_origem_proced = 7;

if (ie_opcao_p = 'G') then
	ds_campo_w	:= substr(cd_grupo_w,1,2);
elsif (ie_opcao_p = 'S') then
	ds_campo_w	:= substr(cd_subgrupo_w,1,2) || '.' || substr(cd_subgrupo_w,3,2);
elsif (ie_opcao_p = 'F') then
	ds_campo_w	:= substr(cd_forma_organizacao_w,1,2) || '.' ||
				substr(cd_forma_organizacao_w,3,2) || '.' ||
				substr(cd_forma_organizacao_w,5,2);
end if;

return ds_campo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
