SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_etnia ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*IE_OPCAO_P:
C - Código
D - Descrição
*/
cd_etnia_w		varchar(4)	:= '0000';
ds_etnia_w		varchar(100);
ds_retorno_w		varchar(100);


BEGIN

begin
select	cd_etnia,
	ds_etnia
into STRICT	cd_etnia_w,
	ds_etnia_w
from	sus_etnia	b,
	pessoa_fisica	a
where	a.nr_seq_etnia		= b.nr_sequencia
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
exception
	when others then
		cd_etnia_w	:= '0000';
end;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:=	cd_etnia_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:=	ds_etnia_w;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
