CREATE OR REPLACE FUNCTION sus_obter_fabricante_prestador ( cd_estabelecimento_p bigint, cd_prestador_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


cd_fabricante_w		varchar(14);
nr_seq_grupo_w		bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_forma_org_w	bigint;

C01 CURSOR FOR
	SELECT	cd_fabricante
	from	sus_opm_fabricante
	where	cd_prestador					= cd_prestador_p
	and (coalesce(nr_seq_grupo, nr_seq_grupo_w)		= nr_seq_grupo_w)
	and (coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)	= nr_seq_subgrupo_w)
	and (coalesce(nr_seq_forma_org, nr_seq_forma_org_w)	= nr_seq_forma_org_w)
	and (coalesce(cd_procedimento, cd_procedimento_p)	= cd_procedimento_p)
	and (coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p)
	order by
		cd_prestador,
		coalesce(cd_procedimento, 0),
		coalesce(nr_seq_forma_org, 0),
		coalesce(nr_seq_subgrupo, 0),
		coalesce(nr_seq_grupo, 0);


BEGIN

/* Obter a estrutura de procedimento SUS Unificado*/

begin
select	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org
into STRICT	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w
from	sus_estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
end;

open C01;
loop
fetch C01 into
	cd_fabricante_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_fabricante_w	:= cd_fabricante_w;
	end;
end loop;
close C01;

return	cd_fabricante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
