CREATE OR REPLACE FUNCTION sus_obter_fpo_regra_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);
qt_procedimento_w		bigint;


BEGIN

begin
select	coalesce(count(*),0)
into STRICT	qt_procedimento_w
from	sus_fpo_regra_proc
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	ie_situacao		= 'A'
and	nr_seq_regra	= nr_seq_regra_p;
exception
when others then
	qt_procedimento_w := 0;
end;

if (coalesce(qt_procedimento_w,0) > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
