CREATE OR REPLACE FUNCTION sus_obter_fpo_regra_setor ( cd_setor_atendimento_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);
qt_setor_w		bigint;
qt_regra_w		bigint;


BEGIN

begin
select	coalesce(count(*),0)
into STRICT	qt_regra_w
from	sus_fpo_regra_setor
where	ie_situacao = 'A'
and	nr_seq_regra	= nr_seq_regra_p;
exception
when others then
	qt_regra_w := 0;
end;

if (qt_regra_w > 0) then
	select	coalesce(count(*),0)
	into STRICT	qt_setor_w
	from	sus_fpo_regra_setor
	where	cd_setor_atendimento = cd_setor_atendimento_p
	and	ie_situacao = 'A'
	and	nr_seq_regra	= nr_seq_regra_p;

	if (coalesce(qt_setor_w,0) > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
