CREATE OR REPLACE FUNCTION sus_obter_grupo_bpa_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_grupo_atend_bpa_w	smallint;
ds_grupo_atend_bpa_w	varchar(20);

C01 CURSOR FOR
	SELECT	distinct a.ie_grupo_atend_bpa
	from  	sus_atividade_prof_proc a,
		sus_atividade_profissional b,
		procedimento_paciente c
	where	a.cd_atividade_profissional = b.cd_atividade_profissional
	and	c.cd_atividade_prof_bpa  = b.cd_atividade_profissional
	and	(a.ie_grupo_atend_bpa IS NOT NULL AND a.ie_grupo_atend_bpa::text <> '')
	and	c.nr_atendimento  = nr_atendimento_p;


BEGIN

ds_grupo_atend_bpa_w	:= '';

OPEN C01;
LOOP
FETCH C01 into
	ie_grupo_atend_bpa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		ds_grupo_atend_bpa_w	:= ds_grupo_atend_bpa_w || ie_grupo_atend_bpa_w || ', ';
	end;
END LOOP;
CLOSE C01;

RETURN	ds_grupo_atend_bpa_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
