CREATE OR REPLACE FUNCTION sus_obter_grupo_habilitacao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_grupo_habilitacao_w		varchar(255);
cd_grupo_habilitacao_w		bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	begin
	select	ds_grupo_habilitacao,
		cd_grupo_habilitacao
	into STRICT	ds_grupo_habilitacao_w,
		cd_grupo_habilitacao_w
	from	sus_grupo_habilitacao
	where	nr_sequencia		= nr_sequencia_p;
	exception
		when others then
		ds_retorno_w	:='';
	end;

end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= cd_grupo_habilitacao_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_grupo_habilitacao_w;
end if;


return	ds_retorno_w;

END;
