CREATE OR REPLACE FUNCTION sus_obter_incolaudo_ativa ( cd_inconsistencia_p bigint) RETURNS boolean AS $body$
DECLARE


ie_consiste_w			varchar(1)	:= 'N';
ie_retorno_w			boolean		:= False;
nr_seq_inconsistencia_w		sus_inconsistencia_laudo.nr_sequencia%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_inconsistencia_w
from	sus_inconsistencia_laudo
where	cd_inconsistencia	= cd_inconsistencia_p;

begin
select	ie_consiste
into STRICT	ie_consiste_w
from	sus_inconsist_laudo_estab
where	cd_estabelecimento = cd_estabelecimento_w
and	nr_seq_inconsistencia = nr_seq_inconsistencia_w
and	cd_perfil = obter_perfil_ativo;
exception
when no_data_found then
	begin
	select	coalesce(max(ie_consiste),'N')
	into STRICT	ie_consiste_w
	from	sus_inconsistencia_laudo
	where	cd_inconsistencia	= cd_inconsistencia_p;
	exception
	when others then
		ie_consiste_w := 'N';
	end;
end;

ie_retorno_w	:= ie_consiste_w = 'S';

return	ie_retorno_w;

end;
