CREATE OR REPLACE FUNCTION sus_obter_limite_bpa ( dt_competencia_p timestamp, ie_complexidade_p text, ie_tipo_financiamento_p text, ie_tipo_p bigint, cd_estabelecimento_p bigint, nr_seq_grupo_p bigint, nr_seq_subgrupo_p bigint, nr_seq_forma_org_p bigint, cd_municipio_ibge_p text) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_P
	1 - Quantidade
	2 - Valor
	3 - Sequencia
*/
qt_bpa_competencia_w		bigint;
vl_bpa_competencia_w		double precision;
nr_sequencia_w			bigint;
ds_retorno_w			double precision := 0;

c01 CURSOR FOR
	SELECT	qt_bpa_competencia,
		vl_bpa_competencia,
		nr_sequencia
	from	sus_limite_bpa_comp
	where	coalesce(ie_tipo_financiamento,coalesce(ie_tipo_financiamento_p,'X')) 	= coalesce(ie_tipo_financiamento_p,'X')
	and	coalesce(ie_complexidade,coalesce(ie_complexidade_p,'X'))		= coalesce(ie_complexidade_p,'X')
	and	coalesce(nr_seq_grupo,coalesce(nr_seq_grupo_p,0))		= coalesce(nr_seq_grupo_p,0)
	and	coalesce(nr_seq_subgrupo,coalesce(nr_seq_subgrupo_p,0))	= coalesce(nr_seq_subgrupo_p,0)
	and	coalesce(nr_seq_forma_org,coalesce(nr_seq_forma_org_p,0))	= coalesce(nr_seq_forma_org_p,0)
	and	coalesce(cd_municipio_ibge,coalesce(cd_municipio_ibge_p,'X'))	= coalesce(cd_municipio_ibge_p,'X')
	and	cd_estabelecimento					= cd_estabelecimento_p
	and	trunc(dt_competencia,'month') 			= trunc(dt_competencia_p,'month')
	order by coalesce(qt_bpa_competencia,0);


BEGIN

open c01;
loop
fetch c01 into
	qt_bpa_competencia_w,
	vl_bpa_competencia_w,
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_tipo_p	= 1) then
		ds_retorno_w	:= qt_bpa_competencia_w;
	elsif (ie_tipo_p	= 2) then
		ds_retorno_w	:= vl_bpa_competencia_w;
	elsif (ie_tipo_p	= 3) then
		ds_retorno_w	:= nr_sequencia_w;
	end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
