CREATE OR REPLACE FUNCTION sus_obter_mot_cobranca_unif ( cd_motivo_cobranca_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_cobranca_w	varchar(100)	:= '';


BEGIN
if (cd_motivo_cobranca_p IS NOT NULL AND cd_motivo_cobranca_p::text <> '') then
    	select	coalesce(max(ds_motivo_cobranca),0)
    	into STRICT	ds_motivo_cobranca_w
    	from	SUS_MOTIVO_COBR_UNIF
    	where	cd_motivo_cobranca	= cd_motivo_cobranca_p;
end if;

return	ds_motivo_cobranca_w;

END;
