CREATE OR REPLACE FUNCTION sus_obter_mot_enc_laudo ( ie_tipo_laudo_apac_p bigint ) RETURNS bigint AS $body$
DECLARE


cd_motivo_cobranca_w		smallint;


BEGIN

begin
select	max(cd_motivo_cobranca)
into STRICT	cd_motivo_cobranca_w
from	sus_regra_mot_enc_laudo
where	ie_tipo_laudo_apac	= ie_tipo_laudo_apac_p
and	ie_situacao	= 'A';
exception
when others then
	cd_motivo_cobranca_w := 12;
end;

return cd_motivo_cobranca_w;

end;
$body$
