SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_municipio_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
				

BEGIN 
 
select 	coalesce(max(cd_pessoa_fisica),0) 
into STRICT	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
if (ie_opcao_p = 'C') then 
	select 	coalesce(obter_compl_pf(cd_pessoa_fisica_w,1,'CDM'),'X') 
	into STRICT	ds_retorno_w 
	;	
else 
	select 	coalesce(obter_compl_pf(cd_pessoa_fisica_w,1,'DM'), wheb_mensagem_pck.get_texto(70430))--'NÃ£o Informado') 
	into STRICT	ds_retorno_w 
	;	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
