CREATE OR REPLACE FUNCTION sus_obter_mun_estab_aih ( cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_municipio_ibge_w	varchar(6);
ds_retorno_w		varchar(100);

/*Opção
C = Código
D = Descrição*/
BEGIN

select	max(cd_municipio_ibge)
into STRICT	cd_municipio_ibge_w
from	sus_parametros_aih
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_opcao_p = 'D') and (coalesce(cd_municipio_ibge_w,'') <> '') then
	select	obter_sus_municipio(cd_municipio_ibge_w)
	into STRICT 	ds_retorno_w
	;
else
	ds_retorno_w := cd_municipio_ibge_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
