CREATE OR REPLACE FUNCTION sus_obter_orgao_emissor_aih (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_estabelecimento_w		smallint;
cd_pessoa_fisica_w		varchar(10);
cd_municipio_ibge_w		varchar(06);
cd_orgao_emissor_aih_w		varchar(10);
				

BEGIN 
 
select	max(cd_estabelecimento), 
	max(cd_pessoa_fisica) 
into STRICT	cd_estabelecimento_w, 
	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	max(cd_municipio_ibge) 
into STRICT	cd_municipio_ibge_w 
from	compl_pessoa_fisica 
where	ie_tipo_complemento	= 1 
and	cd_pessoa_fisica	= cd_pessoa_fisica_w;
 
if (cd_municipio_ibge_w IS NOT NULL AND cd_municipio_ibge_w::text <> '') then 
	select	max(cd_orgao_emissor) 
	into STRICT	cd_orgao_emissor_aih_w 
	from	sus_municipio 
	where	cd_municipio_ibge	= cd_municipio_ibge_w;
end if;
 
if (coalesce(cd_orgao_emissor_aih_w::text, '') = '') then 
	select	max(cd_orgao_emissor_aih) 
	into STRICT	cd_orgao_emissor_aih_w 
	from	sus_parametros_aih 
	where	cd_estabelecimento = cd_estabelecimento_w;
end if;
 
RETURN	cd_orgao_emissor_aih_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
