SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_orig_proc_cat_bpa ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS PROCEDIMENTO.IE_ORIGEM_PROCED%TYPE AS $body$
DECLARE


ie_origem_proced_w		procedimento.ie_origem_proced%type := 1;
cd_convenio_sus_w		parametro_faturamento.cd_convenio_sus%type;
cd_convenio_w			convenio.cd_convenio%type;
nr_conta_sus_w			conta_paciente.nr_interno_conta%type;
ie_tipo_convenio_w			convenio.ie_tipo_convenio%type;
ie_tipo_convenio_ww		convenio.ie_tipo_convenio%type;
cd_categoria_w			atend_categoria_convenio.cd_categoria%type;
cd_categoria_ww			atend_categoria_convenio.cd_categoria%type;
ie_cons_conv_conta_entrada_w	sus_parametros_bpa.ie_cons_conv_conta_entrada%type := 'N';
ie_tipo_atendimento_w		atendimento_paciente.ie_tipo_atendimento%type;
					

BEGIN

begin
select	coalesce(ie_cons_conv_conta_entrada,'N')
into STRICT	ie_cons_conv_conta_entrada_w
from	sus_parametros_bpa
where	cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	ie_cons_conv_conta_entrada_w := 'N';
end;

begin
select	max(a.ie_tipo_convenio),
	max(b.cd_convenio),
	max(b.cd_categoria),
	max(a.ie_tipo_atendimento)
into STRICT	ie_tipo_convenio_w,
	cd_convenio_w,
	cd_categoria_w,
	ie_tipo_atendimento_w
from	atendimento_paciente a,
	atend_categoria_convenio b
where	a.nr_atendimento = b.nr_atendimento
--and	sysdate between nvl(b.dt_inicio_vigencia,sysdate) and nvl(b.dt_final_vigencia,sysdate)
and	b.nr_seq_interno = Obter_Atecaco_atendimento(a.nr_atendimento)
and	a.nr_atendimento = nr_atendimento_p
and	a.cd_estabelecimento = cd_estabelecimento_p;
exception
when others then
	ie_tipo_convenio_w	:= 0;
	cd_convenio_w		:= 0;
	cd_categoria_w		:= '0';
	ie_tipo_atendimento_w	:= 0;
end;

if (ie_cons_conv_conta_entrada_w = 'S') then
	begin
	
	begin
	select	coalesce(max(cd_convenio_sus),0)
	into STRICT	cd_convenio_sus_w
	from	parametro_faturamento
	where	cd_estabelecimento = cd_estabelecimento_p;
	exception
	when others then
		cd_convenio_sus_w := 0;
	end;

	if (cd_convenio_sus_w > 0) then
		begin
	
		begin
		select	coalesce(max(nr_interno_conta),0)
		into STRICT	nr_conta_sus_w
		from	conta_paciente
		where	nr_atendimento = nr_atendimento_p
		and	cd_convenio_parametro = cd_convenio_sus_w;	
		exception
		when others then
			nr_conta_sus_w := 0;
		end;
		
		if (nr_conta_sus_w > 0) then
			begin
			
			begin
			select	ie_tipo_convenio
			into STRICT	ie_tipo_convenio_ww
			from	convenio
			where	cd_convenio = cd_convenio_sus_w  LIMIT 1;
			exception
			when others then
				ie_tipo_convenio_ww := 0;
			end;
			
			begin
			select	cd_categoria_parametro
			into STRICT	cd_categoria_ww
			from	conta_paciente
			where	nr_interno_conta = nr_conta_sus_w  LIMIT 1;
			exception
			when others then
				cd_categoria_ww := '0';
			end;
			
			ie_origem_proced_w := coalesce(obter_origem_proced_cat(cd_estabelecimento_p,
									ie_tipo_atendimento_w,
									ie_tipo_convenio_ww,
									cd_convenio_sus_w,
									cd_categoria_ww),1);
			end;
		else
			begin
			ie_origem_proced_w := coalesce(obter_origem_proced_cat(cd_estabelecimento_p,
									ie_tipo_atendimento_w,
									ie_tipo_convenio_w,
									cd_convenio_sus_w,
									cd_categoria_w),1);
			end;
		end if;
		end;
	else
		begin
		ie_origem_proced_w := coalesce(obter_origem_proced_cat(cd_estabelecimento_p,
								ie_tipo_atendimento_w,
								ie_tipo_convenio_w,
								cd_convenio_sus_w,
								cd_categoria_w),1);
		end;
	end if;
	end;
else
	begin
	ie_origem_proced_w := coalesce(obter_origem_proced_cat(cd_estabelecimento_p,
							ie_tipo_atendimento_w,
							ie_tipo_convenio_w,
							cd_convenio_w,
							cd_categoria_w),1);
	end;
end if;

return	ie_origem_proced_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_orig_proc_cat_bpa ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;

