CREATE OR REPLACE FUNCTION sus_obter_parametros_apac (cd_estabelecimento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(255);
ie_exporta_resp_w 	varchar(1);


BEGIN

select	coalesce(max(ie_exporta_resp),'N')
into STRICT	ie_exporta_resp_w
from	sus_parametros_apac
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_opcao_p	= 1) then
	begin
	ds_retorno_w	:= ie_exporta_resp_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
