CREATE OR REPLACE FUNCTION sus_obter_param_apac ( ds_campo_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(255);

C01 CURSOR FOR
	SELECT	cd_cnes_hospital,
		cd_diretor_clinico,
		cd_interface_envio,
		cd_medico_autorizador,
		cd_medico_responsavel,
		cd_municipio_ibge,
		cd_orgao_emissor_apac,
		cd_unidade_apac,
		cd_unidade_solic_apac,
		ds_versao,
		dt_competencia_apac,
		ie_exp_cnes_prest_apac,
		ie_exporta_resp,
		ie_inc_proc_conta_data,
		ie_orgao_destino,
		ie_restinge_competencia,
		ie_sigla_apac,
		nm_orgao_destino
	from	sus_parametros_apac
	where	cd_estabelecimento = cd_estabelecimento_p;

c01_w c01%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (upper(ds_campo_p)	= 'CD_CNES_HOSPITAL') then
	ds_retorno_w	:= coalesce(c01_w.cd_cnes_hospital,0);
elsif (upper(ds_campo_p)	= 'CD_DIRETOR_CLINICO') then
	ds_retorno_w	:= coalesce(c01_w.cd_diretor_clinico,'');
elsif (upper(ds_campo_p)	= 'CD_INTERFACE_ENVIO') then
	ds_retorno_w	:= coalesce(c01_w.cd_interface_envio,0);
elsif (upper(ds_campo_p)	= 'CD_MEDICO_AUTORIZADOR') then
	ds_retorno_w	:= coalesce(c01_w.cd_medico_autorizador,'');
elsif (upper(ds_campo_p)	= 'CD_MEDICO_RESPONSAVEL') then
	ds_retorno_w	:= coalesce(c01_w.cd_medico_responsavel,'');
elsif (upper(ds_campo_p)	= 'CD_MUNICIPIO_IBGE') then
	ds_retorno_w	:= coalesce(c01_w.cd_municipio_ibge,0);
elsif (upper(ds_campo_p)	= 'CD_ORGAO_EMISSOR_APAC') then
	ds_retorno_w	:= coalesce(c01_w.cd_orgao_emissor_apac,'');
elsif (upper(ds_campo_p)	= 'CD_UNIDADE_APAC') then
	ds_retorno_w	:= coalesce(c01_w.cd_unidade_apac,'');
elsif (upper(ds_campo_p)	= 'CD_UNIDADE_SOLIC_APAC') then
	ds_retorno_w	:= coalesce(c01_w.cd_unidade_solic_apac,'');
elsif (upper(ds_campo_p)	= 'DS_VERSAO') then
	ds_retorno_w	:= coalesce(c01_w.ds_versao,'');
elsif (upper(ds_campo_p)	= 'DT_COMPETENCIA_APAC') then
	ds_retorno_w	:= coalesce(to_char(c01_w.dt_competencia_apac,'dd/mm/yyyy'),'');
elsif (upper(ds_campo_p)	= 'IE_EXP_CNES_PREST_APAC') then
	ds_retorno_w	:= coalesce(c01_w.ie_exp_cnes_prest_apac,'N');
elsif (upper(ds_campo_p)	= 'IE_EXPORTA_RESP') then
	ds_retorno_w	:= coalesce(c01_w.ie_exporta_resp,'C');
elsif (upper(ds_campo_p)	= 'IE_INC_PROC_CONTA_DATA') then
	ds_retorno_w	:= coalesce(c01_w.ie_inc_proc_conta_data,'N');
elsif (upper(ds_campo_p)	= 'IE_ORGAO_DESTINO') then
	ds_retorno_w	:= coalesce(c01_w.ie_orgao_destino,'');
elsif (upper(ds_campo_p)	= 'IE_RESTINGE_COMPETENCIA') then
	ds_retorno_w	:= coalesce(c01_w.ie_restinge_competencia,'N');
elsif (upper(ds_campo_p)	= 'IE_SIGLA_APAC') then
	ds_retorno_w	:= coalesce(c01_w.ie_sigla_apac,'');
elsif (upper(ds_campo_p)	= 'NM_ORGAO_DESTINO') then
	ds_retorno_w	:= coalesce(c01_w.nm_orgao_destino,'');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
