CREATE OR REPLACE FUNCTION sus_obter_ponto_sadt ( nr_seq_proc_p bigint) RETURNS bigint AS $body$
DECLARE

			 
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
dt_procedimento_w	timestamp;
qt_ponto_w		smallint	:= 0;
qt_procedimento_w	double precision	:= 0;
cd_estabelecimento_w	smallint;

BEGIN
 
select	a.cd_procedimento, 
	a.ie_origem_proced, 
	a.dt_procedimento, 
	a.qt_procedimento, 
	b.cd_estabelecimento 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	dt_procedimento_w, 
	qt_procedimento_w, 
	cd_estabelecimento_w 
from	procedimento_paciente a, 
	conta_paciente b 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	nr_sequencia		= nr_seq_proc_p;
 
begin 
select	coalesce(qt_ponto,0) 
into STRICT	qt_ponto_w 
from	sus_aih_sadt 
where	cd_procedimento		= cd_procedimento_w 
and	ie_origem_proced	= ie_origem_proced_w 
and	cd_estabelecimento	= cd_estabelecimento_w 
and	dt_procedimento_w between dt_vigencia_inicial and coalesce(dt_vigencia_final, dt_procedimento_w) 
and	qt_ponto		> 0;
exception 
	when no_data_found then 
		qt_ponto_w	:= 0;
	when others then 
		/*R.aise_application_error(-20011,'Problema no cadastro de SADT para o procedimento ' || cd_procedimento_w || '.' || chr(13) || 
					'Não pode existir registro duplicado para o mesmo procedimento dentro de uma mesma competência #@#@');*/
 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263561,'cd_procedimento_w='||cd_procedimento_w);
end;
 
return(qt_ponto_w * qt_procedimento_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
