CREATE OR REPLACE FUNCTION sus_obter_preco_proced ( cd_estabelecimento_p bigint, dt_procedimento_p timestamp, ie_tipo_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*	ie_opcao_p

1 - Total Hospitalar
2 - Valor Ambulatorial
3 - Vl. SP
4 -  Vl SH
5 - Vl. SA
6 - Vl. SADT
7 - Pontos Ato
*/
vl_sa_w				double precision	:= 0;
vl_sh_w				double precision	:= 0;
vl_sp_w				double precision	:= 0;
vl_sadt_w			double precision	:= 0;
vl_total_hospitalar_w		double precision	:= 0;
vl_total_amb_w			double precision	:= 0;
qt_pontos_ato_w			integer	:= 0;
vl_honorario_medico_w		double precision	:= 0;
vl_matmed_w			double precision	:= 0;
vl_retorno_w			double precision	:= 0;
dt_competencia_sus_w		timestamp;
vl_anestesista_w			double precision	:= 0;


BEGIN

SELECT * FROM Sus_Define_Preco_Proced(	dt_procedimento_p, cd_procedimento_p, ie_origem_proced_p, cd_estabelecimento_p, ie_tipo_atendimento_p, vl_sa_w, vl_sh_w, vl_sp_w, vl_sadt_w, vl_total_hospitalar_w, vl_total_amb_w, qt_pontos_ato_w, vl_honorario_medico_w, vl_matmed_w, vl_anestesista_w, dt_competencia_sus_w) INTO STRICT vl_sa_w, vl_sh_w, vl_sp_w, vl_sadt_w, vl_total_hospitalar_w, vl_total_amb_w, qt_pontos_ato_w, vl_honorario_medico_w, vl_matmed_w, vl_anestesista_w, dt_competencia_sus_w;

if (ie_opcao_p		= 1) then
	vl_retorno_w		:= vl_total_hospitalar_w;
elsif (ie_opcao_p		= 2) then
	vl_retorno_w		:= vl_total_amb_w;
elsif (ie_opcao_p		= 3) then
	vl_retorno_w		:= vl_sp_w;
elsif (ie_opcao_p		= 4) then
	vl_retorno_w		:= vl_sh_w;
elsif (ie_opcao_p		= 5) then
	vl_retorno_w		:= vl_sa_w;
elsif (ie_opcao_p		= 6) then
	vl_retorno_w		:= vl_sadt_w;
elsif (ie_opcao_p		= 7) then
	vl_retorno_w		:= qt_pontos_ato_w;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
