CREATE OR REPLACE FUNCTION sus_obter_prest_partic_aih ( nr_sequencia_p bigint, nr_seq_partic_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_estabelecimento_w		smallint;
cd_medico_executor_w		varchar(10);
cd_cgc_prestador_w		varchar(14);
ie_doc_executor_w		smallint;
ds_retorno_w			varchar(20);

C01 CURSOR FOR 
	SELECT	c.cd_estabelecimento, 
		p.cd_cgc_prestador, 
		coalesce(p.cd_medico_executor,p.cd_pessoa_fisica), 
		coalesce(p.ie_doc_executor,5) 
	from	procedimento_paciente p, 
		conta_paciente c 
	where	p.nr_sequencia		= nr_sequencia_p 
	and	p.nr_interno_conta	= c.nr_interno_conta 
	and	coalesce(nr_seq_partic_p::text, '') = '' 
	
union
 
	SELECT	c.cd_estabelecimento, 
		coalesce(b.cd_cgc,a.cd_cgc_prestador), 
		coalesce(b.cd_pessoa_fisica,a.cd_pessoa_fisica), 
		coalesce(b.ie_doc_executor,5) 
	from	procedimento_participante	b, 
		procedimento_paciente		a, 
		conta_paciente c 
	where	a.nr_sequencia		= b.nr_sequencia 
	and	a.nr_interno_Conta 	= c.nr_interno_conta 
	and	a.nr_sequencia		= nr_sequencia_p 
	and	b.nr_seq_partic		= nr_seq_partic_p;

 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	cd_estabelecimento_w, 
	cd_Cgc_prestador_w, 
	cd_medico_Executor_w, 
	ie_doc_executor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;
 
 
if (ie_doc_executor_w	= 1) then	 
	ds_retorno_w		:= Obter_Cpf_Pessoa_Fisica(cd_medico_executor_w);
elsif (ie_doc_executor_w	= 3) then 
	ds_retorno_w		:= cd_cgc_prestador_w;
elsif (ie_doc_executor_w	= 5) then 
	ds_retorno_w		:= Obter_cnes_estab(cd_estabelecimento_w);
elsif (ie_doc_executor_w	= 6) then 
	select	max(cd_cnes) 
	into STRICT	ds_retorno_w 
	from	pessoa_juridica 
	where	cd_cgc		= cd_cgc_prestador_w;
end if;
	 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
