CREATE OR REPLACE FUNCTION sus_obter_procconta_rateio_aih ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



vl_medico_w		double precision	:= 0;
qt_ponto_medico_w	bigint	:= 0;
qt_ponto_anest_w	bigint	:= 0;
ds_retorno_w		varchar(1)	:= 'N';
cd_procedimento_w	bigint;
qt_reg_proc_w		bigint	:= 0;
cd_registro_proc_w	sus_valor_proc_paciente.cd_registro_proc%type := 0;


BEGIN

begin
select	coalesce(b.vl_medico,0),
	coalesce(b.qt_ato_medico,0),
	coalesce(b.qt_ato_anestesista,0),
	coalesce(a.cd_procedimento,0),
	coalesce(b.cd_registro_proc,0)
into STRICT	vl_medico_w,
	qt_ponto_medico_w,
	qt_ponto_anest_w,
	cd_procedimento_w,
	cd_registro_proc_w
from	sus_valor_proc_paciente	b,
	procedimento_paciente	a
where	a.nr_sequencia		= b.nr_sequencia
and	a.nr_sequencia		= nr_sequencia_p
and	a.ie_origem_proced	= 7
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';
exception
	when others then
	vl_medico_w		:= 0;
	qt_ponto_medico_w	:= 0;
	qt_ponto_anest_w	:= 0;
	cd_registro_proc_w	:= 0;
end;

if	((qt_ponto_medico_w > 0) or (qt_ponto_anest_w > 0) or (cd_procedimento_w in (802010199))) then
	ds_retorno_w	:= 'S';
end if;

if (ds_retorno_w = 'S') and (cd_registro_proc_w <> 3) then
	begin

	select	count(1)
	into STRICT	qt_reg_proc_w
	from	sus_procedimento_registro
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = 7
	and	cd_registro in (3,4);

	if (qt_reg_proc_w > 1) then
		ds_retorno_w	:= 'N';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
