CREATE OR REPLACE FUNCTION sus_obter_procedimento ( cd_consulta_publica_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE



cd_procedimento_w		bigint;
ds_retorno_w			varchar(240);



BEGIN

select	coalesce(max(b.cd_procedimento),0)
into STRICT	cd_procedimento_w
from	procedimento		b,
	sus_procedimento	a
where	a.cd_consulta_publica	= cd_consulta_publica_p
and	a.cd_procedimento	= b.cd_procedimento
and	a.ie_origem_proced	= b.ie_origem_proced
and	b.ie_situacao		= 'A';

ds_retorno_w	:= cd_procedimento_w;

if (ie_retorno_p	= 'D') then
	select	coalesce(max(ds_procedimento),'')
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_w;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
