SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_proced_aih_eis ( nr_interno_conta_p bigint, ie_tipo_proc_aih_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_proc_aih_p
	1 - Solicitado
	2 - Realizado
*/
/* ie_retorno_p
	C - Código
	D - Descrição
*/
nr_aih_w		bigint;
nr_seq_aih_w		bigint;
cd_procedimento_solic_w	bigint := null;
cd_procedimento_real_w	bigint := null;
cd_procedimento_w	bigint := null;
ds_retorno_w		varchar(255) := '';


BEGIN

begin
select	nr_aih,
	nr_sequencia
into STRICT	nr_aih_w,
	nr_seq_aih_w
from	sus_aih_unif
where	nr_interno_conta	= nr_interno_conta_p;
exception
	when others then
	nr_aih_w	:= 0;
	nr_seq_aih_w	:= 0;
end;

if (nr_aih_w	> 0) then

	select	cd_procedimento_solic,
		cd_procedimento_real
	into STRICT	cd_procedimento_solic_w,
		cd_procedimento_real_w
	from	sus_aih_unif
	where	nr_aih		= nr_aih_w
	and	nr_sequencia	= nr_seq_aih_w;

	if (ie_tipo_proc_aih_p	= 1) then
		cd_procedimento_w	:= cd_procedimento_solic_w;
	elsif (ie_tipo_proc_aih_p	= 2) then
		cd_procedimento_w	:= cd_procedimento_real_w;
	end if;

	if (ie_retorno_p	= 'C') then
		ds_retorno_w	:= cd_procedimento_w;
	elsif (ie_retorno_p	= 'D') then
		ds_retorno_w	:= substr(obter_descricao_procedimento(cd_procedimento_w, 7),1,255);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
