CREATE OR REPLACE FUNCTION sus_obter_proced_apac_unif ( nr_interno_conta_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE



/* ie_retorno_p
	C - Código
	D - Descrição
*/
cd_procedimento_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

begin
select	coalesce(cd_procedimento,'')
into STRICT	cd_procedimento_w
from	sus_apac_unif
where	nr_interno_conta	= nr_interno_conta_p;
exception
	when others then
	cd_procedimento_w	:= 0;
end;

if (ie_retorno_p	= 'C') then
	ds_retorno_w	:= cd_procedimento_w;
elsif (ie_retorno_p	= 'D') then
	ds_retorno_w	:= substr(obter_descricao_procedimento(cd_procedimento_w, 7),1,255);
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
