DECLARE


cd_procedimento_solic_w	bigint;
cd_procedimento_real_w	bigint;
ds_retorno_w		varchar(15);


BEGIN

select	coalesce(max(cd_procedimento_solic),0),
	coalesce(max(cd_procedimento_real),0)
into STRICT	cd_procedimento_solic_w,
	cd_procedimento_real_w
from	sus_aih_unif
where	nr_interno_conta	= nr_interno_conta_p;

