CREATE OR REPLACE FUNCTION sus_obter_proc_laudosolic_aih ( nr_aih_p bigint, nr_seq_aih_p bigint, ie_tipo_retorno_p bigint) RETURNS varchar AS $body$
DECLARE



/* ie_tipo_retorno_p
	1 - Codigo
	2 - Descrição
*/
cd_procedimento_w	bigint;
ds_procedimento_w	procedimento.ds_procedimento%TYPE;
ds_retorno_w		varchar(240);


BEGIN

begin
select	cd_procedimento_solic,
	substr(obter_descricao_procedimento(cd_procedimento_solic, ie_origem_proced),1,255) ds_procedimento_solic
into STRICT	cd_procedimento_w,
	ds_procedimento_w
from	sus_laudo_paciente
where	nr_aih		= nr_aih_p
and (coalesce(nr_seq_aih,0) = 0 or
	nr_seq_aih	 = nr_seq_aih_p)
and	ie_tipo_laudo_sus	= 0;
exception
	when others then
	cd_procedimento_w	:= 0;
	ds_procedimento_w	:= '';
end;

if (ie_tipo_retorno_p	= 1) then
	ds_retorno_w	:= cd_procedimento_w;
elsif (ie_tipo_retorno_p	= 2) then
	ds_retorno_w	:= ds_procedimento_w;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
