SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_qt_atos_aih ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint	:= 0;
qt_aih_unif_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_aih_unif_w
from	sus_aih_unif
where	nr_interno_conta	= nr_interno_conta_p;


if (qt_aih_unif_w = 0) then
	begin
	select	sum(qt_procedimento)
	into STRICT	qt_retorno_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	ie_origem_proced	= 2
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	exception
		when others then
		qt_retorno_w	:= 0;
	end;
else
	begin
	select	sum(qt_procedimento)
	into STRICT	qt_retorno_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	ie_origem_proced	= 7
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	exception
		when others then
		qt_retorno_w	:= 0;
	end;

end if;

return	qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_qt_atos_aih ( nr_interno_conta_p bigint) FROM PUBLIC;

