CREATE OR REPLACE FUNCTION sus_obter_qt_proc_laudo (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_proc_laudo_w		bigint := 0;			
qt_proc_adic_w		bigint := 0;			
qt_retorno_w		bigint := 0;			
			 

BEGIN 
 
select	coalesce(sum(qt_procedimento_solic),0) 
into STRICT	qt_proc_laudo_w 
from	sus_laudo_paciente 
where	nr_atendimento = nr_atendimento_p 
and	cd_procedimento_solic = cd_procedimento_p 
and	ie_origem_proced = ie_origem_proced_p;
 
select	coalesce(sum(b.qt_procedimento),0) 
into STRICT	qt_proc_adic_w 
from	sus_laudo_paciente a, 
	sus_laudo_proced_adic b 
where	a.nr_atendimento = nr_atendimento_p 
and	a.nr_seq_interno = b.nr_seq_laudo 
and	b.cd_procedimento = cd_procedimento_p 
and	b.ie_origem_proced = ie_origem_proced_p;
 
qt_retorno_w := coalesce(qt_proc_laudo_w,0) + coalesce(qt_proc_adic_w,0);
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
