SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_raca_sinan (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_cor_pele_w	bigint := 0;
ie_raca_sinan_w		smallint := 0;
ds_valor_dominio_w	varchar(254);
vl_dominio_w		varchar(15);
ds_retorno_w		varchar(254) := '';
ie_opcao_w		varchar(1);



BEGIN

select	coalesce(nr_seq_cor_pele,0)
into STRICT	nr_seq_cor_pele_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nr_seq_cor_pele_w <> 0) then
	begin
	select	coalesce(ie_raca_sinan,0)
	into STRICT	ie_raca_sinan_w
	from	cor_pele
	where	nr_sequencia = nr_seq_cor_pele_w;
	end;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := ie_raca_sinan_w;
elsif (ie_opcao_p = 'D')  then
	begin

	begin
	select	substr(ds_valor_dominio,1,254)
	into STRICT	ds_valor_dominio_w
	from	valor_dominio
	where	cd_dominio = 2610
	and	vl_dominio = ie_raca_sinan_w;
	exception
	when others then
		ds_valor_dominio_w := '';
	end;

	ds_retorno_w := ds_valor_dominio_w;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
