CREATE OR REPLACE FUNCTION sus_obter_regra_expbpa_proc ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nr_seq_subgrupo_w		bigint;
nr_seq_forma_org_w		bigint;
nr_seq_regra_w			bigint;
ie_complexidade_w			varchar(2);
ie_exclui_procedimento_w		varchar(1)	:= 'N';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_exclui_procedimento
	from	sus_regra_exportacao_bpa
	where (coalesce(nr_seq_grupo, nr_seq_grupo_w)		= nr_seq_grupo_w)
	and (coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)	= nr_seq_subgrupo_w)
	and (coalesce(nr_seq_forma_org, nr_seq_forma_org_w)	= nr_seq_forma_org_w)
	and (coalesce(cd_procedimento, cd_procedimento_p)	= cd_procedimento_p)
	and (coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p)
	and (coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p)
	and	((coalesce(ie_complexidade::text, '') = '') or (ie_complexidade	= ie_complexidade_w))
	order by	coalesce(cd_procedimento, 0),
		coalesce(ie_complexidade,''),
		coalesce(nr_seq_forma_org, 0),
		coalesce(nr_seq_subgrupo, 0),
		coalesce(nr_seq_grupo, 0);


BEGIN

/* Obter a estrutura do procedimento*/

begin
select	c.nr_seq_grupo,
	b.nr_seq_subgrupo,
	a.nr_seq_forma_org,
	a.ie_complexidade
into STRICT	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w,
	ie_complexidade_w
from 	sus_procedimento a,
	sus_forma_organizacao b,
	sus_subgrupo c,
	sus_grupo d
where	a.cd_procedimento 	= cd_procedimento_p
and	a.ie_origem_proced 	= ie_origem_proced_P
and	a.nr_seq_forma_org	= b.nr_sequencia
and	b.nr_seq_subgrupo	= c.nr_sequencia
and	c.nr_seq_grupo		= d.nr_sequencia;

exception
	when others then
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
end;

open c01;
loop
fetch c01 into
	nr_seq_regra_w,
	ie_exclui_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return	ie_exclui_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
