CREATE OR REPLACE FUNCTION sus_obter_regra_expbpa ( nr_seq_proc_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_grupo_w			bigint;
nr_seq_subgrupo_w		bigint;
nr_seq_forma_org_w		bigint;
cd_estabelecimento_w		smallint;
nr_seq_regra_w			bigint;
ie_complexidade_w			varchar(2);
ie_exclui_procedimento_w		varchar(1)	:= 'N';	
					 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_exclui_procedimento 
	from	sus_regra_exportacao_bpa 
	where (coalesce(nr_seq_grupo, nr_seq_grupo_w)		= nr_seq_grupo_w) 
	and (coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)	= nr_seq_subgrupo_w) 
	and (coalesce(nr_seq_forma_org, nr_seq_forma_org_w)	= nr_seq_forma_org_w) 
	and (coalesce(cd_procedimento, cd_procedimento_w)	= cd_procedimento_w) 
	and (coalesce(ie_origem_proced, ie_origem_proced_w)	= ie_origem_proced_w) 
	and (coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w) 
	and	((coalesce(ie_complexidade::text, '') = '') or (ie_complexidade	= ie_complexidade_w)) 
	order by	coalesce(cd_procedimento, 0), 
		coalesce(ie_complexidade,''), 
		coalesce(nr_seq_forma_org, 0), 
		coalesce(nr_seq_subgrupo, 0), 
		coalesce(nr_seq_grupo, 0);
					

BEGIN 
 
/* Obter dados do procedimento lan√ßado*/
 
select	a.cd_procedimento, 
	a.ie_origem_proced, 
	c.cd_estabelecimento, 
	sus_obter_complexidade_proced(a.cd_procedimento,a.ie_origem_proced,'C') 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_estabelecimento_w, 
	ie_complexidade_w 
from	atendimento_paciente	c, 
	conta_paciente		b, 
	procedimento_paciente	a 
where	a.nr_sequencia	= nr_seq_proc_p 
and	a.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_atendimento	= c.nr_atendimento;
 
/* Obter a estrutura do procedimento*/
 
begin 
select	nr_seq_grupo, 
	nr_seq_subgrupo, 
	nr_seq_forma_org 
into STRICT	nr_seq_grupo_w, 
	nr_seq_subgrupo_w, 
	nr_seq_forma_org_w 
from	sus_estrutura_procedimento_v 
where	cd_procedimento	= cd_procedimento_w 
and	ie_origem_proced	= ie_origem_proced_w;
exception 
	when others then 
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
end;
 
open c01;
loop 
fetch c01 into	 
	nr_seq_regra_w, 
	ie_exclui_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;
 
return	ie_exclui_procedimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
