SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_regra_exp_exec ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_medico_executor_p text, cd_cbo_p text, cd_estabelecimento_p bigint, ie_aih_p text, ie_bpa_p text, ie_apac_p text, tp_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* Opções de retorno:
M - Médico
C - CBO
CNS - Cartão Nascional SUS
*/
ds_retorno_w		varchar(255) := '';
ie_exclui_medico_w		varchar(1)	:= 'N';
ie_exclui_cnes_w		varchar(1)	:= 'N';
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_forma_org_w	bigint;
cd_estabelecimento_w	smallint;
ie_credenciamento_w	varchar(3);
cd_medico_executor_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
cd_profissional_w		varchar(10);
qt_proc_serv_classif_w	integer	:= 0;
ie_serv_classif_w		varchar(1)	:= 'N';
nr_seq_regra_w		bigint;
cd_cbo_executor_w	varchar(6);
ie_considerar_cns_ant_w	parametro_faturamento.ie_considerar_cns_ant%type;
nr_cartao_nac_sus_ant_w cnes_profissional.nr_cartao_nac_sus_ant%type;

C01 CURSOR FOR
	SELECT	/*+INDEX (SUSREEX_PK)*/		cd_medico_exec_exp,
		cd_cbo_exec_exp
	from	sus_regra_exp_exec
	where (coalesce(nr_seq_grupo, nr_seq_grupo_w)			= nr_seq_grupo_w)
	and (coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)		= nr_seq_subgrupo_w)
	and (coalesce(nr_seq_forma_org, nr_seq_forma_org_w)		= nr_seq_forma_org_w)
	and (coalesce(cd_procedimento, cd_procedimento_p)		= cd_procedimento_p)
	and (coalesce(ie_origem_proced, ie_origem_proced_p)		= ie_origem_proced_p)
	and (coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p)
	and (coalesce(cd_medico_exec, coalesce(cd_medico_executor_p,0))	= coalesce(cd_medico_executor_p,0))
	and (coalesce(cd_cbo_exec, coalesce(cd_cbo_p,0))			= coalesce(cd_cbo_p,0))
	and	(((coalesce(ie_aih,'N')	= ie_aih_p) and (ie_aih_p	= 'S'))
	or	((coalesce(ie_bpa,'N')	= ie_bpa_p) and (ie_bpa_p	= 'S'))
	or	((coalesce(ie_apac,'N')	= ie_apac_p) and (ie_apac_p	= 'S')))
	order by
		coalesce(cd_procedimento, 0),
		coalesce(nr_seq_forma_org, 0),
		coalesce(nr_seq_subgrupo, 0),
		coalesce(nr_seq_grupo, 0);


BEGIN

/* Obter a estrutura do procedimento*/

begin
select	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org
into STRICT	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w
from	sus_estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
end;

begin
select	coalesce(ie_considerar_cns_ant, 'N')
into STRICT	ie_considerar_cns_ant_w
from	parametro_faturamento
where	cd_estabelecimento=wheb_usuario_pck.get_cd_estabelecimento;
exception
	when others then
	ie_considerar_cns_ant_w :='N';
end;

open C01;
loop
fetch C01 into
	cd_medico_executor_w,
	cd_cbo_executor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_medico_executor_w	:= cd_medico_executor_w;
	cd_cbo_executor_w	:= cd_cbo_executor_w;
	end;
end loop;
close C01;

if (tp_retorno_p = 'M') then
	ds_retorno_w	:= cd_medico_executor_w;
elsif (tp_retorno_p = 'C') then
	ds_retorno_w	:= cd_cbo_executor_w;
elsif (tp_retorno_p = 'CNS') then
	begin
		begin
		select	coalesce(nr_cartao_nac_sus,'')
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_medico_executor_w  LIMIT 1;
		exception
		when others then
			ds_retorno_w := '';
		end;

		if (ie_considerar_cns_ant_w = 'S') then
			begin
				begin
					select	coalesce(b.nr_cartao_nac_sus_ant, ' ')
					into STRICT	nr_cartao_nac_sus_ant_w
					from 	cnes_profissional 	b
					where	b.cd_pessoa_fisica = cd_medico_executor_w  LIMIT 1;
					exception
				when others then
					nr_cartao_nac_sus_ant_w := ' ';
				end;

				if (nr_cartao_nac_sus_ant_w <> ' ') then
					ds_retorno_w := nr_cartao_nac_sus_ant_w;
				end if;
			end;
		end if;
	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
