CREATE OR REPLACE FUNCTION sus_obter_regra_fatura (cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_complexidade_p text, ie_tipo_financiamento_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w			bigint;
nr_seq_forma_org_w		bigint := 0;
nr_seq_grupo_w			bigint := 0;
nr_seq_subgrupo_w		bigint := 0;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	sus_regra_fatura_item a,
		sus_regra_fatura b
	where	a.nr_seq_regra_fatura 	= b.nr_sequencia
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(a.cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
	and	((coalesce(a.cd_procedimento::text, '') = '') or (coalesce(a.ie_origem_proced,ie_origem_proced_p) = ie_origem_proced_p))
	and	coalesce(a.nr_seq_grupo,nr_seq_grupo_w)			= nr_seq_grupo_w
	and	coalesce(a.nr_seq_subgrupo,nr_seq_subgrupo_w)		= nr_seq_subgrupo_w
	and	coalesce(a.nr_seq_forma_org,nr_seq_forma_org_w)		= nr_seq_forma_org_w
	and	coalesce(a.ie_complexidade,ie_complexidade_p)		= ie_complexidade_p
	and	coalesce(a.ie_tipo_financiamento,ie_tipo_financiamento_p)	= ie_tipo_financiamento_p
	order by 	coalesce(a.cd_procedimento,0),
			coalesce(a.nr_seq_forma_org,0),
			coalesce(a.nr_seq_subgrupo,0),
			coalesce(a.nr_seq_grupo,0),
			coalesce(a.ie_tipo_financiamento,'0'),
			coalesce(a.ie_complexidade,'0');


BEGIN

begin
select	nr_seq_forma_org,
	nr_seq_grupo,
	nr_seq_subgrupo
into STRICT	nr_seq_forma_org_w,
	nr_seq_grupo_w,
	nr_seq_subgrupo_w
from	sus_estrutura_procedimento_v a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	null;
end;

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return nr_seq_regra_w;

END;
$body$
