SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_seq_estrut_proc ( cd_parametro_p bigint, ie_estrutura_p text) RETURNS bigint AS $body$
DECLARE





/* IE_ESTRUTURA_P
        F - Forma organizacao
        S - SubGrupo
        G - Grupo
*/
nr_sequencia_w          bigint;



BEGIN

if (ie_estrutura_p = 'F') then
        select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	sus_forma_organizacao
	where	cd_forma_organizacao	= cd_parametro_p;
elsif (ie_estrutura_p = 'S') then
        select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	sus_subgrupo
	where	cd_subgrupo	= cd_parametro_p;
elsif (ie_estrutura_p = 'G') then
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	sus_grupo
	where	cd_grupo	= cd_parametro_p;
end if;

return nr_sequencia_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
