CREATE OR REPLACE FUNCTION sus_obter_serv_classif_apac ( nr_seq_apac_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_servico_w		bigint;
nr_seq_servico_classif_w	bigint;
ds_retorno_w			varchar(255) := '';


BEGIN

select	coalesce(max(a.nr_seq_servico),0),
	coalesce(max(a.nr_seq_servico_classif),0)
into STRICT	nr_seq_servico_w,
	nr_seq_servico_classif_w
from	procedimento_paciente a,
	sus_apac_unif b
where	b.cd_procedimento = a.cd_procedimento
and	b.ie_origem_proced = a.ie_origem_proced
and	a.nr_interno_conta = b.nr_interno_conta
and	b.nr_sequencia = nr_seq_apac_p;

begin
if (ie_opcao_p = 'CS') then
	begin
	ds_retorno_w := substr(sus_obter_dados_servico(nr_seq_servico_w,'C'),1,255);
	end;
elsif (ie_opcao_p = 'DS') then
	begin
	ds_retorno_w := substr(sus_obter_dados_servico(nr_seq_servico_w,'D'),1,255);
	end;
elsif (ie_opcao_p = 'CC') then
	begin
	ds_retorno_w := substr(sus_obter_desc_classif(nr_seq_servico_classif_w,null,'C'),1,255);
	end;
elsif (ie_opcao_p = 'DC') then
	begin
	select	substr(max(ds_servic_classif),1,255)
	into STRICT	ds_retorno_w
	from	sus_servico_classif
	where	nr_sequencia	= nr_seq_servico_classif_w;
	end;
end if;
exception
when others then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
