CREATE OR REPLACE FUNCTION sus_obter_se_apac_laudo_vig (nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_registro_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN 
 
select	count(*) 
into STRICT	qt_registro_w 
from	sus_apac_unif a, 
	sus_laudo_paciente b 
where	a.nr_atendimento = b.nr_atendimento 
and	a.cd_procedimento = b.cd_procedimento_solic 
and	a.cd_cid_principal = b.cd_cid_principal 
and	coalesce(a.dt_ocorrencia::text, '') = '' 
and	trunc(clock_timestamp()) between trunc(a.dt_inicio_validade) and trunc(a.dt_fim_validade) 
and	b.nr_seq_interno = nr_seq_interno_p;
 
if (qt_registro_w > 0) then 
	begin 
	ds_retorno_w := 'S';
	end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
