CREATE OR REPLACE FUNCTION sus_obter_se_atend_recente ( cd_pessoa_fisica_p text , nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE

 
nr_atend_retorno_w		bigint := 0;					
					 

BEGIN 
 
select 	coalesce(max(a.nr_atendimento),0) 
into STRICT	nr_atend_retorno_w 
from 	atendimento_paciente a, 
	atend_categoria_convenio b 
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	a.nr_atendimento	= b.nr_atendimento	 
and	exists (SELECT	1 
		from	atendimento_paciente c 
		where	c.nr_atendimento = nr_atendimento_p 
		and	a.dt_alta	< c.dt_entrada 
		and	obter_hora_entre_datas(a.dt_alta,c.dt_entrada) < 72) 
and	a.ie_tipo_atendimento		= 1 
and	obter_tipo_convenio(b.cd_convenio) 	= 3;
 
return nr_atend_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
