SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_se_const_proc_bpa ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registro_w		numeric(20);
nr_seq_grupo_w		bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_forma_org_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	sus_regra_consist_proc_bpa
where	cd_estabelecimento	= cd_estabelecimento_p
and	ie_situacao		= 'A';

if (coalesce(qt_registro_w,0) > 0) then
	begin
	/* Obter a estrutura do procedimento*/

	begin
	select	nr_seq_grupo,
		nr_seq_subgrupo,
		nr_seq_forma_org
	into STRICT	nr_seq_grupo_w,
		nr_seq_subgrupo_w,
		nr_seq_forma_org_w
	from	sus_estrutura_procedimento_v
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;
	exception
		when others then
		nr_seq_grupo_w		:= 0;
		nr_seq_subgrupo_w	:= 0;
		nr_seq_forma_org_w	:= 0;
	end;

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	sus_regra_consist_proc_bpa
	where (coalesce(nr_seq_grupo, nr_seq_grupo_w)			= nr_seq_grupo_w)
	and (coalesce(nr_seq_subgrupo, nr_seq_subgrupo_w)		= nr_seq_subgrupo_w)
	and (coalesce(nr_seq_forma_org, nr_seq_forma_org_w)		= nr_seq_forma_org_w)
	and (coalesce(cd_procedimento, cd_procedimento_p)		= cd_procedimento_p)
	and (coalesce(ie_origem_proced, ie_origem_proced_p)		= ie_origem_proced_p)
	and (coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p)
	and	ie_situacao		= 'A'
	order by
		coalesce(cd_procedimento, 0),
		coalesce(nr_seq_forma_org, 0),
		coalesce(nr_seq_subgrupo, 0),
		coalesce(nr_seq_grupo, 0);
	end;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
