CREATE OR REPLACE FUNCTION sus_obter_se_cons_laudo_estab ( nr_seq_inconsistencia_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_consiste_w		varchar(1);
ie_permite_liberar_w	varchar(1);


BEGIN

begin
select	ie_consiste,
	ie_permite_liberar
into STRICT	ie_consiste_w,
	ie_permite_liberar_w
from	sus_inconsist_laudo_estab
where	nr_seq_inconsistencia	= nr_seq_inconsistencia_p
and	cd_estabelecimento		= cd_estabelecimento_p
and	cd_perfil 			= obter_perfil_ativo;
exception
	when others then
	ie_consiste_w		:= null;
	ie_permite_liberar_w	:= null;
end;

if (ie_opcao_p	= 'C') then
	begin
	ds_retorno_w	:= ie_consiste_w;
	end;
elsif (ie_opcao_p	= 'L') then
	begin
	ds_retorno_w	:= ie_permite_liberar_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
