CREATE OR REPLACE FUNCTION sus_obter_se_emissaorn_valida ( nr_atendimento_p bigint, dt_emissao_rn_p timestamp) RETURNS varchar AS $body$
DECLARE

 
 
dt_entrada_w		timestamp;
dt_alta_w		timestamp;
ds_retorno_w		varchar(2)	:= 'S';	
	 

BEGIN 
 
select	dt_entrada, 
	coalesce(dt_alta,clock_timestamp()) 
into STRICT	dt_entrada_w, 
	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento		= nr_atendimento_p;
 
if (dt_emissao_rn_p not between trunc(dt_entrada_w, 'dd') and fim_dia(dt_alta_w)) then 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
