CREATE OR REPLACE FUNCTION sus_obter_se_executor_crm ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := 'S';
nr_seq_conselho_w		pessoa_fisica.nr_seq_conselho%type;
sg_conselho_w		conselho_profissional.sg_conselho%type;


BEGIN

select	coalesce(nr_seq_conselho,0)
into STRICT	nr_seq_conselho_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p  LIMIT 1;

select	trim(both upper(sg_conselho))
into STRICT	sg_conselho_w
from	conselho_profissional
where	nr_sequencia	= nr_seq_conselho_w  LIMIT 1;

if (coalesce(sg_conselho_w,'X')	<> 'CRM') then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
