SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_se_gerar_proc_laudo ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_classif_laudo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := 'N';
nr_seq_forma_org_w	bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_grupo_w		bigint;
qt_regra_w		bigint;
cd_estabelecimento_w	smallint;


BEGIN

begin
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
exception
when others then
	cd_estabelecimento_w := null;
end;

select	a.nr_sequencia,
	b.nr_sequencia,
	c.nr_sequencia
into STRICT	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w
from	sus_grupo a,
	sus_subgrupo b,
	sus_forma_organizacao c,
	sus_procedimento d
where	a.nr_sequencia = b.nr_seq_grupo
and	b.nr_sequencia = c.nr_seq_subgrupo
and	c.nr_sequencia = d.nr_seq_forma_org
and	d.cd_procedimento = cd_procedimento_p
and	d.ie_origem_proced = ie_origem_proced_p;

select	count(*)
into STRICT	qt_regra_w
from	sus_regra_gerar_proc_laudo
where	coalesce(nr_seq_grupo,nr_seq_grupo_w) 		= nr_seq_grupo_w
and	coalesce(nr_seq_subgrupo,nr_seq_subgrupo_w) 	= nr_seq_subgrupo_w
and	coalesce(nr_seq_forma_org,nr_seq_forma_org_w) 	= nr_seq_forma_org_w
and	coalesce(cd_procedimento,cd_procedimento_p) 	= cd_procedimento_p
and	coalesce(ie_origem_proced,ie_origem_proced_p) 	= ie_origem_proced_p
and	cd_estabelecimento = coalesce(cd_estabelecimento_w,cd_estabelecimento)
and	((ie_laudo_aih = 'S' AND ie_classif_laudo_p = 1) or
	((ie_laudo_apac	= 'S') and (ie_classif_laudo_p in (11,12,13,14))) or
	(ie_laudo_bpa = 'S' AND ie_classif_laudo_p = 15))
and	ie_situacao = 'A';

if (qt_regra_w > 0) then
	ds_retorno_w := 'S';
end if;

return	coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
