CREATE OR REPLACE FUNCTION sus_obter_se_gera_novo_atend ( cd_pessoa_fisica_p bigint, cd_cid_principal_p SUS_LAUDO_PACIENTE.CD_CID_PRINCIPAL%TYPE) RETURNS varchar AS $body$
DECLARE

 
ie_permite_w			varchar(1) := 'N';
nr_atend_ant_w			bigint;
ie_atend_vinc_aghos_w		varchar(1);
cd_proced_laudo_w		bigint;
cd_cid_principal_w		sus_laudo_paciente.cd_cid_principal%type;
nr_atend_pa_amb_w		bigint;


BEGIN 
if (coalesce(cd_pessoa_fisica_p,0) > 0) then 
 
	select	max(nr_atendimento) 
	into STRICT	nr_atend_ant_w 
	from	atendimento_paciente 
	where  cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	ie_tipo_atendimento = 1 
	and (clock_timestamp() - dt_alta) <= 3;
	 
	if (coalesce(nr_atend_ant_w,0) > 0) then 
	 
		begin 
			select	'S', 
				nr_atend_original 
			into STRICT	ie_atend_vinc_aghos_w, 
				nr_atend_pa_amb_w 
			from	solicitacao_tasy_aghos 
			where	nr_atendimento = nr_atend_ant_w  LIMIT 1;			
		exception 
			when	no_data_found then 
					ie_atend_vinc_aghos_w := 'N';
		end;	
		 
		if (ie_atend_vinc_aghos_w = 'S') and (coalesce(nr_atend_pa_amb_w,0) > 0)then 
						 
			select	max(cd_cid_principal) 
			into STRICT	cd_cid_principal_w 
			from	sus_laudo_paciente 
			where	nr_atendimento = nr_atend_pa_amb_w 
			and	ie_classificacao = 1;
			 
			if (cd_cid_principal_w = cd_cid_principal_p) then 
				ie_permite_w	:= 'S';			
			end if;
			 
		end if;
		 
	end if;
	 
end if;
 
return	ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
