CREATE OR REPLACE FUNCTION sus_obter_se_proc_bpa_duplo ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1) := 'N';
qt_reg_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	sus_procedimento_registro  a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p
and	a.cd_registro in (1,2);

if (qt_reg_w = 2) then
	ds_retorno_w := 'S';
end if;

return	coalesce(ds_retorno_w,'N');

end;
