CREATE OR REPLACE FUNCTION sus_obter_se_proc_classif_hosp ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15)	:= 'S';
nr_seq_serv_classif_w	bigint;
qt_proc_classif_serv_w	bigint;

c01 CURSOR FOR
	SELECT	nr_seq_serv_classif
	from	sus_proced_serv_classif
	where	cd_procedimento	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_serv_classif_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_proc_classif_serv_w
	from	sus_serv_classif_hosp a,
		sus_servico_hospital b
	where	a.nr_seq_serv_hosp	= b.nr_sequencia
	and	a.nr_seq_serv_classif	= nr_seq_serv_classif_w
	and	b.cd_estabelecimento	= cd_estabelecimento_p;

	if (qt_proc_classif_serv_w = 0) then
		ds_retorno_w	:= 'N';
	end if;

	qt_proc_classif_serv_w	:= 0;

	end;
end loop;
close C01;

return	coalesce(ds_retorno_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
